  </div>
</template>

<script setup>
import { ref, onMounted } from "vue";
import router from "./router/index";
import { useRoute, RouterView } from "vue-router";
import { useAuthStore } from "./stores/authStore";
import api from "./api/api";

const authStore = useAuthStore();
const route = useRoute();
const showSidebar = ref(false);
const showDropdown = ref(false);
const accountDropdown = ref(false);
const showSearch = ref(false);

onMounted(() => {
  authStore.loginFromStorage();

  const dropdownBtn = document.querySelectorAll(".dropdown-btn");
  dropdownBtn.forEach((btn) => {
    btn.addEventListener("click", () => {
      btn.classList.toggle("open");
      const downArrow = btn.querySelector(".arrow-right");
      downArrow.classList.toggle("open");
      btn.nextElementSibling.classList.toggle("open");
    });
  });
});

function toggleSidebar() {
  showSidebar.value = !showSidebar.value;
}

function toggleDropdown() {
  showDropdown.value = !showDropdown.value;
}

function toggleAccountDropdown() {
  accountDropdown.value = !accountDropdown.value;
}

async function logout() {
  console.log("caling logout");
  const response = await api.auth.logout();

  console.log("what is response", response);
  if (response.status == 200) {
    authStore.logout();
    router.push("/login");
  }
}

const toggleSearch = () => {
  showSearch.value = !showSearch.value;
};

const checkActive = () => {
  const check = ["/games", "/users", "/roles"].includes(route.fullPath);

  if(check) {
    return {
      backgroundColor: "#f3f3ff !important",
      color: "#696cff !important",
      fill: "#696cff !important"
    };
  }

  if (!check) {
    return {
      backgroundColor: "transparent !important",
      color: "#697A8D !important",
      fill: "#697A8D !important"
    };
  }
};
</script>
